import {
    getTasksServer,
    postTaskServer,
    updateTaskServer,
    deleteTaskServer,
    getTaskServer
  } from "../ServerCall";
import {updateTaskItem, addTaskItem, deleteTaskItem, completeTaskItem} from './state';

const initState = {
    todos: [
        {id: 1, 
        title: "New task...",
        status: "new",
        body: "Add notes..."}
    ],
    //getTasksServer().then(tasks =>
    activeTodo: todos[0]
}

const newTask = () => {
    return {
      title: "New task...",
      status: "new",
      body: "Add notes..."
    };
  };

const TODO_ADD = 'TODO_ADD';
const TODO_UPDATE = 'TODO_UPDATE';
const TODO_DELETE = 'TODO_DELETE';
const TODO_COMPLETE = 'TODO_COMPLETE'

export default (state = initState, action) => {
    switch (action.type){
        case TODO_ADD:
            return {...state, todos: addTaskItem(state.todos)};
            // return {...state, todos: state.todos.concat(newTask)}
        case TODO_UPDATE:
            return {...state, todos: updateTaskItem(state.todos, state.activeTodo, action.payload.title, action.payload.body)};
        case TODO_DELETE:
            return {...state, todos: deleteTaskItem(state.todos, state.activeTodo)};
        case TODO_COMPLETE:
        default:
            return state;
    };
}
