import {getTasksServer} from "../ServerCall";
import {updateTaskItem, addTaskItem, deleteTaskItem, completeTaskItem} from '../state';

const initState = {
    todos: [
        {id: 1, 
        title: "New task...",
        status: "new",
        body: "Add notes..."}
    ],
    //getTasksServer().then(tasks =>
    activeTodo: []
    // todos[0]
}

const TODO_ADD = 'TODO_ADD';
const TODO_SELECT = 'TODO_SELECT'
const TODO_UPDATE = 'TODO_UPDATE';
const TODO_DELETE = 'TODO_DELETE';
const TODO_COMPLETE = 'TODO_COMPLETE';

// these will replace functions in App.js
export async function addTask() {
    return {type: TODO_ADD};
}

export function selectTask(item) {
    return {type: TODO_ADD, payload: item}
  }

export function updateTask(item, title, body) {
    return {type: TODO_UPDATE, payload: {item, title, body}};
  }

export function deleteTask(item) {
    return {type: TODO_DELETE, payload: item}
  }

export function completeTask() {
    return {type: TODO_COMPLETE}
  }

// needs to be async function w/ await for each case?
export default (state = initState, action) => {
    switch (action.type){
        case TODO_ADD:
            return {...state, todos: addTaskItem(state.todos)};
            // return {...state, todos: state.todos.concat(newTask)}
        case TODO_SELECT:
            return {...state, activeTodo: action.payload}
        case TODO_UPDATE:
            return {...state, todos: updateTaskItem(state.todos, state.activeTodo, action.payload.title, action.payload.body)};
        case TODO_DELETE:
            return {...state, todos: deleteTaskItem(state.todos, state.activeTodo)};
        case TODO_COMPLETE:
            return {...state, todos: completeTaskItem(state.todos, state.activeTodo)};
        default:
            return state;
    };
}
