import React, { useState, useEffect } from "react";
import "./styles.css";

import TodoList from "./components/TodoList";
import TodoDetail from "./components/TodoDetail";
import {
  getTasksServer,
  postTaskServer,
  updateTaskServer,
  deleteTaskServer,
  getTaskServer
} from "./ServerCall";
import {updateTaskItem, addTaskItem, deleteTaskItem, completeTaskItem} from './state';

const newTask = () => {
  return {
    title: "New task...",
    status: "new",
    body: "Add notes..."
  };
};

export default function App() {
  const [items, setItems] = useState(undefined);
  const [activeItem, setActiveItem] = useState(undefined);

  function addTask() {
    // postTaskServer(newTask()).then(item => setItems([...items, item]));
    const nextItems = addTaskItem(items);
    // setActiveItem(nextItems.find(obj => obj._id === item._id));
    setItems(nextItems);
  }
  function selectTask(item) {
    setActiveItem(item);
  }
  function updateTask(item, title, body) {
    const nextItems = updateTaskItem(items, item, title, body);
    setActiveItem(nextItems.find(obj => obj._id === item._id));
    setItems(nextItems);
    // let copyOfItem = { ...item };
    // if (title !== undefined) {
    //   copyOfItem.title = title;
    // }
    // if (body !== undefined) {
    //   copyOfItem.body = body;
    // }
    // setActiveItem(copyOfItem);
    // items[items.findIndex(obj => obj._id === copyOfItem._id)] = copyOfItem;
    // updateTaskServer(copyOfItem);
    // setItems([...items]);
  }
  function deleteTask(item) {
    // let copyOfItems = items.filter(i => i._id !== item._id);
    // deleteTaskServer(item);
    const nextItems = deleteTaskItem(items, item);
    setItems(nextItems);
    setActiveItem(nextItems[0]);
    // setActiveItem(copyOfItems[0]);
  }
  function completeTask(item) {
    // item.status = "completed";
    // updateTaskServer(item);
    const nextItems = completeTaskItem(items, item)
    setItems(nextItems);
  }

  useEffect(() => {
      getTasksServer().then(tasks => {
        setItems(tasks);
        setActiveItem(tasks[0]);
      });
    }, []);

  // function loadApp() {
  //   getTasksServer().then(tasks => {
  //     setItems(tasks);
  //     setActiveItem(tasks[0]);
  //   }, []);
  // }

  return (
    <div className="wrapper">
      {/* <button onClick={() => loadApp()}>Load</button> */}
      <TodoList
        items={items}
        onAdd={addTask}
        onSelect={selectTask}
        onDelete={deleteTask}
      />
      <TodoDetail
        active={activeItem}
        onUpdateTask={updateTask}
        onComplete={completeTask}
        onDelete={deleteTask}
      />
    </div>
  );
}
