import { TODO_ADD, TODO_SELECT, TODO_UPDATE, TODO_DELETE, TODO_COMPLETE } from "../actionTypes";

export async function addTask(items) {
    return {type: TODO_ADD, payload: items};
}

export function selectTask(item) {
    return {type: TODO_SELECT, payload: item}
  }

export function updateTask(items) {
    return {type: TODO_UPDATE, payload: items};
  }

export function deleteTask(items) {
    return {type: TODO_DELETE, payload: items}
  }

export function completeTask(items) {
    return {type: TODO_COMPLETE, payload: items}
  }
